import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'

const EditBlog = ({ blogs, setBlogs }) => {
    const { id } = useParams();
    const [blog, setBlog] = useState({ title: ' ', content: ' ', comments: [] });
    useEffect(() => {
        const blog = blogs.find(b => b.id === Number.parseInt(id));
        setBlog(blog);
    }, [])
    const navigate = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        const answer = window.confirm('Are you sure you want to save blog');
        if (answer) {
            setBlogs(prev => {
                const temp = [...prev];
                const index = prev.findIndex(b => b.id === Number.parseInt(id));
                temp[index] = blog;
                return temp;
            })
            navigate('/')
        }

    }
    const handleChange = (e) => {
        setBlog(prev => {
            return { ...prev, [e.target.name]: e.target.value };
        })
    }
    return (
        <div className='form-wrapper'>
            <h3>Edit Blog</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    placeholder='Title'
                    className='form-control'
                    name='title'
                    value={blog.title}
                    onChange={handleChange} />
                <textarea
                    placeholder='Enter content'
                    value={blog.content}
                    name='content'
                    onChange={handleChange}></textarea>
                <button>Save</button>
            </form>
        </div>
    )
}

export default EditBlog
