import React from 'react';
import blogItem from './blogItem';

const List = ({ blogs, setBlogs }) => {
    const handleDelete = async (id) => {
        let index = blogs.findIndex(blog => blog.id === id);
        if (index > -1) {
            const answer = window.confirm('Are you sure you want to delete blog with id ' + id);
            if (answer) {
                setBlogs(prev => {
                    let temp = [...prev];
                    temp.splice(index, 1);
                    return temp;
                })
            }
        }
    }
    return (
        <div className='wrapper'>
            <h3>List Blogs</h3>
            <ul>
                {
                    blogs && blogs.map(blog => <blogItem key={blog.id} blog={blog} handleDelete={handleDelete} />)
                }
            </ul>
        </div>
    )
}
export default List;
